     create table if not exists usuarios (
         id int8 generated by default as identity,
         email varchar(255),
         nome varchar(255),
         telefone varchar(255),
         endereco varchar(255),
         senha varchar(255),
         status varchar(255),
         permissao varchar(255),
         created_at timestamp not null,
         updated_at timestamp not null,
         primary key (id)
     );

